import * as React from "react";
import { useForm } from "react-hook-form";
import { Link, useLocation } from "react-router-dom";
import styled from "styled-components";
import AuthLayout from "../auth/AuthLayout";
import HeaderContainer from "../auth/HeaderContainer";
import PageTitle from "../shared/PageTitle";

const FormBox = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

const Input = styled.input`
  width: 292px;
  padding: 10px 0 10px 0;
  border-radius: 18px;
  background-color: #f5f5f5;
  margin-bottom: 8px;
  &::placeholder {
    font-size: 14px;
  }
  &:focus {
    border-color: gray;
  }
`;

const Button = styled.input`
  width: 292px;
  padding: 10px 0 10px 0;
  border-radius: 18px;
  color: ${(props) => props.ftcolor};
  font-weight: bold;
  margin-bottom: 8px;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  background-color: ${(props) => props.color};
`;

const BottomBox = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  margin-bottom: 40px;
`;

const BottomButton = styled(Link)`
  width: 292px;
  padding: 10px;
  border-radius: 18px;
  color: ${(props) => props.ftcolor};
  font-weight: bold;
  margin-bottom: 8px;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: ${(props) => props.color};
`;

const Separator = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 300px;
  margin: 12px 0 20px 0;
  span {
    font-size: 12px;
    color: black;
    margin: 0 20px;
    font-weight: 600;
  }
  div {
    width: 100%;
    height: 0.5px;
    background-color: black;
  }
`;

const FindLink = styled.div`
  margin: 10px 0 10px 0;
  a {
    color: black;
    font-size: 12px;
    padding: 5px;
  }
`;

function Login() {
  const location = useLocation();

  const {
    register,
    handleSubmit,
    formState,
    getValues,
    setError,
    clearErrors,
  } = useForm({
    mode: "onChange",
  });
  return (
    <AuthLayout>
      <PageTitle title="Login" />
      <HeaderContainer />

      <FormBox>
        <Input
          {...register("id", {
            required: "ID is required.",
          })}
          type="text"
          placeholder="id"
          hasError={Boolean(formState.errors?.id?.message)}
        />
        <Input
          {...register("password", {
            required: "Password is required.",
          })}
          type="password"
          placeholder="password"
          hasError={Boolean(formState.errors?.password?.message)}
        />
        <Button
          type="submit"
          value={"Log in"}
          color="#6cd2d7"
          ftcolor="white"
        />
      </FormBox>
      <BottomBox>
        <Separator>
          <div></div>
          <span>OR</span>
          <div></div>
        </Separator>
        <BottomButton to="/signup" color="#0D655C" ftcolor="white">
          Sign up
        </BottomButton>
        <BottomButton to="/" color="#FAE100" ftcolor="black">
          Kakao Log in
        </BottomButton>
        <FindLink>
          <Link to="/">아이디 찾기</Link>|<Link to="/">비밀번호 찾기</Link>
        </FindLink>
      </BottomBox>
    </AuthLayout>
  );
}
export default Login;
